INSERT INTO `wp_posts` VALUES ('14', '1', '2018-04-10 01:47:05', '2018-04-10 01:47:05', 'Bãi Trước (hay còn gọi là bãi Tầm Dương) nằm giữa núi Lớn và núi Nhỏ, là một vịnh nhỏ lặng sóng. Bãi Trước nằm trong khoảng từ đèn Xanh (đèn hàng hải) tới cầu Đá, bắt đầu từ số 01 Trần Phú, qua hết đường Quang Trung tới một phần đường Hạ Long. Cảnh quan thiên nhiên ở Bãi Trước ngoài núi Lớn, núi Nhỏ còn có hòn Ngưu nhô hẳn ra ngoài biển có thể coi là một kỳ quan của bãi biển này.', 'Bãi trước Vũng Tàu', '', 'publish', 'open', 'closed', '', 'bai-truoc-vung-tau', '', '', '2018-04-10 12:53:06', '2018-04-10 12:53:06', '', '0', 'http://gonow.vn/?post_type=place&#038;p=14', '0', 'place', '', '0');
