INSERT INTO `wp_posts` VALUES ('27', '1', '2018-04-10 12:56:21', '2018-04-10 12:56:21', 'Thành phố Vũng Tàu thuộc địa phận tỉnh Bà Rịa - Vũng Tàu và nằm ở Đông Nam Bộ Việt Nam. Vũng Tàu chỉ cách Sài Gòn 125km đường bộ, tức là bạn chỉ mất tầm hai giờ đi xe từ thành phố Hồ Chí Minh là có thể vẫy vùng dưới làn nước biển mát rượi và thưởng thức hải sản tươi sống tại đây.', 'Bãi Sau Vũng tàu', '', 'trash', 'open', 'closed', '', 'bai-sau-vung-tau__trashed', '', '', '2018-04-10 13:00:55', '2018-04-10 13:00:55', '', '0', 'http://gonow.vn/?post_type=place&#038;p=27', '0', 'place', '', '0');
