INSERT INTO `wp_posts` VALUES ('40', '1', '2018-04-10 13:13:08', '2018-04-10 13:13:08', 'Trong nhiều thập niên của thế kỷ trước, Côn Đảo thường được nhắc đến như một hòn đảo chuyên giam giữ và tra tấn tù nhân chính trị. Từ hòn đảo biệt lập, đau thương, Côn Đảo đã trở mình thành điểm đến du lịch lý tưởng với những bãi biển tuyệt đẹp, những danh thắng kỳ vĩ và hành trình du lịch tâm linh đầy cuốn hút.', 'Côn Đảo', '', 'publish', 'open', 'closed', '', 'con-dao', '', '', '2018-04-10 13:13:08', '2018-04-10 13:13:08', '', '0', 'http://gonow.vn/?post_type=place&#038;p=40', '0', 'place', '', '0');
