INSERT INTO `wp_posts` VALUES ('66', '1', '2018-04-10 13:34:10', '2018-04-10 13:34:10', 'Quần đảo Bà Lụa với nét đẹp được ví như Vịnh Hạ Long của phương Nam với nhiều hòn đảo lớn nhỏ cùng nằm trong danh sách những điểm đến không thể bỏ qua ở Kiên Giang để bạn tới khám phá. Điểm đặc biệt ở quần đảo Bà Lụa đó chính là bạn có thể tự mình bắt ốc, sò, hàu ngay tại các bãi biển rất dễ dàng. Do mực nước tại các bãi biển này khá thấp, bạn đứng cùng lắm chỉ tới ngang người và nước biển trong vắt nên rất dễ nhìn thấy những con hải sản dưới đáy. Buổi sáng hay chiều bạn nhớ ngồi trước bãi biển để ngắm mặt trời mọc, mặt trời lặn và lưu lại tấm hình lãng mạn nhất nhé.', 'Quần đảo Bà Lụa', '', 'publish', 'open', 'closed', '', 'quan-dao-ba-lua', '', '', '2018-04-10 13:34:10', '2018-04-10 13:34:10', '', '0', 'http://gonow.vn/?post_type=place&#038;p=66', '0', 'place', '', '0');
