INSERT INTO `wp_posts` VALUES ('74', '1', '2018-04-11 06:24:58', '2018-04-11 06:24:58', 'Bãi Rạng hay biển Rạng là bãi tắm đẹp nhất của TP. Phan Thiết. Bãi Rạng cách trung tâm thành phố khoảng 15 km về phía Bắc, nằm dưới những rặng dừa dày đặc trông giống khu rừng dừa rất đẹp. Mùa hè về, bãi Rạng chiều nào cũng tấp nập khách đến tắm biển và thưởng thức món cá chuồn xanh nướng ngọt lịm.', 'Bãi Rạng', '', 'publish', 'open', 'closed', '', 'bai-rang', '', '', '2018-04-11 06:24:58', '2018-04-11 06:24:58', '', '0', 'http://gonow.vn/?post_type=place&#038;p=74', '0', 'place', '', '0');
