INSERT INTO `wp_posts` VALUES ('88', '1', '2018-04-11 06:36:02', '2018-04-11 06:36:02', 'Cù Lao Câu thuộc xã Phước Thể, huyện Tuy Phong, hiện lên như một chiến hạm lớn, xung quanh bao bọc bởi hàng vạn khối đá với màu sắc và hình thù khác nhau. Đây là một đảo vắng, nằm cách bờ biển khoảng 9km, đi tàu từ đất liền ra hết khoảng 1 giờ 30 phút. Trên đảo có hàng ngàn khối đá với hình thù rất độc đáo, xen kẽ là những thảm cỏ xanh mượt. Nơi đây được qui hoạch làm khu bảo tồn sinh vật biển và là điểm du lịch sinh thái biển rất hấp dẫn.', 'Cù Lao Câu', '', 'publish', 'open', 'closed', '', 'cu-lao-cau', '', '', '2018-04-11 06:36:02', '2018-04-11 06:36:02', '', '0', 'http://gonow.vn/?post_type=place&#038;p=88', '0', 'place', '', '0');
