INSERT INTO `wp_posts` VALUES ('92', '1', '2018-04-11 06:39:38', '2018-04-11 06:39:38', 'Khi đặt chân đến Bình Thuận, chắc chắn các bạn sẽ phải đặt chân tới Mũi Né - trung tâm du lịch của tỉnh Bình Thuận. Đến với nơi đây, bạn sẽ được thưởng thức những món đặc sản cùng với đó là các sản vật đặc trưng của Bình Thuận. Đây là một điểm đến lí tưởng dành cho bạn.', 'Mũi Né', '', 'publish', 'open', 'closed', '', 'mui-ne', '', '', '2018-04-11 06:39:38', '2018-04-11 06:39:38', '', '0', 'http://gonow.vn/?post_type=place&#038;p=92', '0', 'place', '', '0');
