INSERT INTO `wp_posts` VALUES ('94', '1', '2018-04-11 06:41:14', '2018-04-11 06:41:14', 'Coco BeachCamp Lagi là một khu cắm trại cao cấp ở Bình Thuận. Tại đây, bạn sẽ thực sự tìm được những phút giây thư giản, đúng nghĩa là một chuyến đi du lịch.', 'Coco BeachCamp Lagi', '', 'publish', 'open', 'closed', '', 'coco-beachcamp-lagi', '', '', '2018-04-11 06:41:14', '2018-04-11 06:41:14', '', '0', 'http://gonow.vn/?post_type=place&#038;p=94', '0', 'place', '', '0');
