INSERT INTO `wp_posts` VALUES ('96', '1', '2018-04-11 06:44:08', '2018-04-11 06:44:08', 'Hòn Mun có làn nước trong veo và hệ sinh thái san hô đẹp lộng lẫy, đã được Quỹ Ðộng vật hoang dã thế giới (WWF) đánh giá là khu vực đa dạng sinh học biển bậc nhất ở Việt Nam. Đến Hòn Mun, du khách không thể bỏ qua những hoạt động như: Bar nổi trên biển, lặn biển khám phá san hô, thuyền đáy kính…', 'Đảo Hòn Mun', '', 'publish', 'open', 'closed', '', 'dao-hon-mun', '', '', '2018-04-11 06:44:08', '2018-04-11 06:44:08', '', '0', 'http://gonow.vn/?post_type=place&#038;p=96', '0', 'place', '', '0');
