INSERT INTO `wp_posts` VALUES ('98', '1', '2018-04-11 06:45:18', '2018-04-11 06:45:18', 'Đảo có diện tích khoảng 110 ha, cách thành phố Du lịch Nha Trang 7km về phía Đông Nam. Từ Hòn Tằm có thể nhìn thấy cả thành phố Nha Trang ở hướng Bắc và bán đảo Cam Ranh ở phía Nam. Ngoài bãi tắm tuyện đẹp, nơi đây còn nổi tiếng với hoạt động khám phá biển bằng tàu đáy kính cũng như dịch vụ lặn biển ngắm san hô.', 'Đảo Hòn Tằm', '', 'publish', 'open', 'closed', '', 'dao-hon-tam', '', '', '2018-04-11 06:45:18', '2018-04-11 06:45:18', '', '0', 'http://gonow.vn/?post_type=place&#038;p=98', '0', 'place', '', '0');
