INSERT INTO `wp_posts` VALUES ('100', '1', '2018-04-11 06:46:38', '2018-04-11 06:46:38', 'Cách thành phố biển Nha Trang khoảng 60 cây số, nằm trên bán đảo Hòn Mèo, vịnh Ninh Vân mang đậm nét hoang sơ và thuần khiết. Nơi đây đặc biệt thích hợp cho những ai muốn tìm cảm giác thư thái trong không gian biển tĩnh lặng. Ngoài ra, bạn cũng có thể ngắm san hô, lặn biển hay thử chơi lướt sóng, lướt ván, đi thuyền Kayak.', 'Vịnh Ninh Vân', '', 'publish', 'open', 'closed', '', 'vinh-ninh-van', '', '', '2018-04-11 06:46:38', '2018-04-11 06:46:38', '', '0', 'http://gonow.vn/?post_type=place&#038;p=100', '0', 'place', '', '0');
