INSERT INTO `wp_posts` VALUES ('104', '1', '2018-04-11 06:49:49', '2018-04-11 06:49:49', 'Vịnh Vân Phong thuộc huyện Vạn Ninh (Khánh Hòa), cách trung tâm thành phố Du lịch Nha Trang khoảng 40km về phía Bắc. Nơi đây thu hút du khách với vẻ đẹp hoang sơ của quần thể đảo lớn nhỏ và màu xanh trong của những vịnh nhỏ hơn ở bên trong.', 'Vịnh Vân Phong', '', 'publish', 'open', 'closed', '', 'vinh-van-phong', '', '', '2018-04-11 06:49:49', '2018-04-11 06:49:49', '', '0', 'http://gonow.vn/?post_type=place&#038;p=104', '0', 'place', '', '0');
