INSERT INTO `wp_posts` VALUES ('106', '1', '2018-04-11 07:27:55', '2018-04-11 07:27:55', 'Bãi biển Đại Lãnh thuộc địa phận huyện Vạn Ninh, tỉnh Khánh Hòa, nằm bên quốc lộ 1A, cách TP Du lịch Nha Trang khoảng 80km về phía Bắc. Sau khi vùng vẫy thỏa thích dưới dòng nước, du khách có thể thuê thuyền máy tham quan làng chải Khải Lương, Đầm Môn, cảng Vũng Rô, hay vào làng Đại Lãnh khám phá đời sống của dân chài…\r\n<div id=\"attachment_118675\" class=\"wp-caption aligncenter\"></div>', 'Bãi biển Đại Lãnh', '', 'publish', 'open', 'closed', '', 'bai-bien-dai-lanh', '', '', '2018-04-11 07:27:55', '2018-04-11 07:27:55', '', '0', 'http://gonow.vn/?post_type=place&#038;p=106', '0', 'place', '', '0');
