INSERT INTO `wp_posts` VALUES ('117', '1', '2018-04-11 07:36:23', '2018-04-11 07:36:23', 'Bãi biển Mỹ Khê cũng không còn là cái tên lạ lẫm gì khi mà nơi đây từng liên tục xuất hiện “nhẵn mặt” trên các báo đài trong và ngoài nước với danh hiệu một trong số những bãi biển quyến rũ nhất hành tinh. Từ thị xã Quảng Ngãi, du khách đi theo quốc lộ 24B tầm 15km là sẽ đến địa phận thôn Cổ Lũy, từ đây bạn có thể dễ dàng đến bãi biển Mỹ Khê bằng cách hỏi người dân địa phương.', 'Bãi biển Mỹ Khê', '', 'publish', 'open', 'closed', '', 'bai-bien-my-khe', '', '', '2018-04-11 07:36:23', '2018-04-11 07:36:23', '', '0', 'http://gonow.vn/?post_type=place&#038;p=117', '0', 'place', '', '0');
