INSERT INTO `wp_posts` VALUES ('127', '1', '2018-04-11 07:43:09', '2018-04-11 07:43:09', 'Được bù đắp từ những con sông mang vẻ đẹp mỹ miều, cửa biển Sa Cần từ lâu đã được coi là một khu du lịch vô cùng lý tưởng và thú vị cho mọi người khám phá, những nét bình yên cùng sự thơ mộng đã mang đến cho nơi này sự bình yên khó phai mờ cho những hành trình khám phá đầy thú vị.', 'Cửa biển Sa Cần', '', 'publish', 'open', 'closed', '', 'cua-bien-sa-can', '', '', '2018-04-11 07:43:09', '2018-04-11 07:43:09', '', '0', 'http://gonow.vn/?post_type=place&#038;p=127', '0', 'place', '', '0');
