INSERT INTO `wp_posts` VALUES ('129', '1', '2018-04-11 07:44:55', '2018-04-11 07:44:55', 'Đến Bình Định mà không chơi ở bãi biển của thành phố Quy Nhơn thì quả là một thiếu sót. Biển ở đây tuy không quá nổi tiếng như bãi biển ở Nha Trang, nhưng nó vẫn sở hữu nét hoang sơ rất đặc biệt, tạo ra sức hút riêng cho Bình Định.', 'Biển Quy Nhơn', '', 'publish', 'open', 'closed', '', 'bien-quy-nhon', '', '', '2018-04-11 07:44:55', '2018-04-11 07:44:55', '', '0', 'http://gonow.vn/?post_type=place&#038;p=129', '0', 'place', '', '0');
