INSERT INTO `wp_posts` VALUES ('131', '1', '2018-04-11 07:46:02', '2018-04-11 07:46:02', 'Sở dĩ nó có cái tên bãi tắm Hoàng Hậu là vì đây đã từng là nơi nghỉ dưỡng riêng của vua Bảo Đại và vị hoàng hậu Nam Phương. Hoàng hậu đã bị vẻ đẹp của cảnh vật, thiên nhiên ở đây hấp dẫn nên đã chọn nơi này để dừng chân, nghỉ dưỡng.', 'Bãi tắm Hoàng Hậu', '', 'publish', 'open', 'closed', '', 'bai-tam-hoang-hau', '', '', '2018-04-11 07:46:02', '2018-04-11 07:46:02', '', '0', 'http://gonow.vn/?post_type=place&#038;p=131', '0', 'place', '', '0');
