INSERT INTO `wp_posts` VALUES ('133', '1', '2018-04-11 07:47:18', '2018-04-11 07:47:18', 'Một <strong>khu du lịch Bình Định</strong> mới khá hấp dẫn khách du lịch tọa lạc trên tại xã Nhơn Lý cách thành phố Quy Nhơn 20km. Eo Gió bắt nguồn từ hình dáng địa lý bởi từ trên cao nhìn xuống ta sẽ thấy eo biển được bao bọc bởi dãy núi như chen chắn và ôm trọng bãi biển tuyệt đẹp.', 'Eo Gió', '', 'publish', 'open', 'closed', '', 'eo-gio', '', '', '2018-04-11 07:47:18', '2018-04-11 07:47:18', '', '0', 'http://gonow.vn/?post_type=place&#038;p=133', '0', 'place', '', '0');
