INSERT INTO `wp_posts` VALUES ('136', '1', '2018-04-11 07:49:03', '2018-04-11 07:49:03', 'Một món quà vô giá mà thiên nhiên đã ban tặng cho mảnh đất Bình Định. Cù Lao Xanh nằm trên xã đảo Nhơn Châu cách thành phố nha trang 20km.', 'Cù Lao Xanh', '', 'publish', 'open', 'closed', '', 'cu-lao-xanh', '', '', '2018-04-11 07:49:03', '2018-04-11 07:49:03', '', '0', 'http://gonow.vn/?post_type=place&#038;p=136', '0', 'place', '', '0');
