INSERT INTO `wp_posts` VALUES ('139', '1', '2018-04-11 07:50:13', '2018-04-11 07:50:13', 'Không phải một <strong>địa điểm du lịch ở Bình Định</strong> được nhiều người biết đến nhưng không vì thế mà Kỳ Co kém hấp dẫn hơn. Cách thành phố 25km thuộc đảo Nhơn Lý. Bãi tắm tại Kỳ Co lặng sóng, biển nông nên du khách có thể thỏa thức tắm mà khô lo sợ sóng biển. Bãi cát trắng nguyên sơ những ngọn núi hiên ngang kỳ vĩ bao quanh càng làm cho vẻ đẹp của Kỳ Co thêm phần thú vị.', 'Kỳ Co', '', 'publish', 'open', 'closed', '', 'ky-co', '', '', '2018-04-11 07:50:13', '2018-04-11 07:50:13', '', '0', 'http://gonow.vn/?post_type=place&#038;p=139', '0', 'place', '', '0');
