INSERT INTO `wp_posts` VALUES ('145', '1', '2018-04-11 09:15:19', '2018-04-11 09:15:19', 'Bãi biển Mỹ Khê là 1 trong số những bãi biển đẹp nhất ở Đà Nẵng, và sẽ thật là quá tiếc nuối nếu như bạn đến Đà Nẵng mà không được tắm biển cũng như vui chơi ở bãi biển Mỹ Khê 1 lần.', 'Bãi biển Mỹ Khê', '', 'publish', 'open', 'closed', '', 'bai-bien-my-khe-2', '', '', '2018-04-11 09:15:19', '2018-04-11 09:15:19', '', '0', 'http://gonow.vn/?post_type=place&#038;p=145', '0', 'place', '', '0');
