INSERT INTO `wp_posts` VALUES ('156', '1', '2018-04-11 09:21:14', '2018-04-11 09:21:14', 'Thuộc phường Phước Mỹ, quận Sơn Trà, tại công viên biển Đông, đây là một bãi tắm công cộng được xây dựng với kinh phí 12 tỉ đồng là địa điểm thu hút cư dân địa phương và khách du lịch khắp nơi. Bãi tắm ở đây có diện tích 7.726m2, có các công trình phụ trợ như một đài phun nước, 6 hồ chứa nước ngọt, 26 dãy vòi sen, 24 phòng thay quần áo bằng composit. Bãi tắm có thể phục vụ từ 3.500 đến 4.000 lượt người đến tắm biển mỗi ngày.', 'Bãi Biển Phạm Văn Đồng', '', 'publish', 'open', 'closed', '', 'bai-bien-pham-van-dong', '', '', '2018-04-11 09:21:14', '2018-04-11 09:21:14', '', '0', 'http://gonow.vn/?post_type=place&#038;p=156', '0', 'place', '', '0');
