INSERT INTO `wp_posts` VALUES ('194', '1', '2018-04-11 09:46:54', '2018-04-11 09:46:54', 'Bãi tắm Đông Hà thuộc huyện Hải Hậu, Nam Định. Đến với Bãi tắm đông Hà bạn có thể ghé thăm khu du lịch nổi tiếng như: Khu du lịch biển Thịnh Long, cầu Ngói – chùa Lương, Đền thánh Hưng Nghĩa.', 'Bãi tắm Đông Hà', '', 'publish', 'open', 'closed', '', 'bai-tam-dong-ha', '', '', '2018-04-11 09:46:54', '2018-04-11 09:46:54', '', '0', 'http://gonow.vn/?post_type=place&#038;p=194', '0', 'place', '', '0');
